def is_valid(x, y, board):
    n = len(board)
    return 0 <= x < n and 0 <= y < n and board[x][y] != 'X'


def find_path(start, end, board):
    n = len(board)
    directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
    queue = deque([(start, [start])])
    visited = set()
    while queue:
