                new_path = path + [(new_x, new_y)]
                queue.append(((new_x, new_y), new_path))
    return None


def fill_board(board):
    n = len(board)
    num_positions = {}
    for i in range(n):
        for j in range(n):
            if board[i][j].isdigit():
                if board[i][j] not in num_positions:
                    num_positions[board[i][j]] = []
                num_positions[board[i][j]].append((i, j))

