def solve_lights_out(board_str):
    board = []
    for row_str in board_str.strip().split('\n'):
        board.append([int(c) for c in row_str])
    n_rows = len(board)
    n_cols = len(board[0])

    def get_neighbors(r, c):
        neighbors = [(r, c)]
        if r > 0: neighbors.append((r - 1, c))
        if r < n_rows - 1: neighbors.append((r + 1, c))
        if c > 0: neighbors.append((r, c - 1))
        if c < n_cols - 1: neighbors.append((r, c + 1))
        return neighbors

    def toggle(current_board, r, c):
        for nr, nc in get_neighbors(r, c):
            current_board[nr][nc] = 1 - current_board[nr][nc]

    def is_all_off(current_board):
        for r in range(n_rows):
            for c in range(n_cols):
                if current_board[r][c] == 1:
                    return False
        return True

    presses = []
    current_board = [row[:] for row in board]

    for r in range(n_rows):
        for c in range(n_cols):
            if current_board[r][c] == 1:
                presses.append((r, c))
                toggle(current_board, r, c)

    if is_all_off(current_board):
        return ", ".join([f"({r},{c})" for r, c in presses])

    return "No solution found"


