                        try
                        {
                            unit.Descriptor.AddMythicExperience(10000, null, new MechanicsContext(RulesetImplementetion.FakeContextEntity(unit.Descriptor), casterChecker));
                        }
                        catch (Exception) { }
                    }
                }

                if (Settings.Instance.hpRollMax)
                {
                    foreach (UnitEntityData unit in Game.Instance.Player.Party)
                    {
                        if (unit.IsPartyLeader()) continue;
                        var stat = unit.Stats.GetStat(StatType.HitPoints);
                        if (stat is ModifiableValueAttributeStat hitPoints)
                        {
                            var baseMax = hitPoints.BaseValue;
                            var mod = hitPoints.ModifiedValue - baseMax;
                            hitPoints.BaseValue = (int)(baseMax * 1.5f);
                            if (Settings.Instance.hpRollMax && hitPoints.ModifiedValue < hitPoints.BaseValue + mod)
                            {
                                hitPoints.UpdateValue();
                            }
                            hitPoints.Refresh();
                        }
                    }
                }

                return false; // Skip the original method
            }
        }

