                throw new ArgumentNullException(nameof(session));
            }

            try
            {
                var (id, exists) = await GetUserIdInternalAsync(session);

                if (exists)
                {
                    await _userCloseService?.CloseUser(session.Jid);

                    var data = await _sessionTable.GetItemAsync<UberStateEntity>(id.ToString(), id.ToString());

                    if (data?.Type == UberStateEntityType.Session)
                    {
                        if (data.SessionId == session.SessionId)
                        {
                            await _sessionTable.DeleteItemAsync<UberStateEntity>(id.ToString(), id.ToString());
                        }
                    }

                }

                //This is the TTL case, we do not have to do anything except notify that the user state may still be around for another hour
                //at most before it is auto-cleaned.
                if (session.SessionId != null && await _notLoggedInUsers.ContainsKeyAsync(session.SessionId))
                {
                    await _notLoggedInUsers.RemoveAsync(session.SessionId);
                }

                //This is the TTL case, we do not have to do anything except notify that the user state may still be around for another hour
