        private async Task<List<T>> ExecuteQuery<T>(QueryDefinition queryDefinition, Func<JsonElement, T> mapper)
        {
            var result = new List<T>();

            Container container = _remoteStateTable.GetContainer();
            using FeedIterator<JsonElement> feedIterator = container.GetItemQueryIterator<JsonElement>(queryDefinition);

            while (feedIterator.HasMoreResults)
            {
                foreach (var item in await feedIterator.ReadNextAsync())
                {
                    result.Add(mapper(item));
                }
            }

            return result;
        }

